--- monster_move table
CREATE TABLE IF NOT EXISTS monster_move
(
    id
        integer
        constraint monster_move_pk
            primary key autoincrement,
    monster_id
        integer
        not null
        constraint
            monster_move_monster_id_fk
        references
            monster,
    move_id
        integer
        not null
        constraint
            monster_move_move_id_fk
        references
            move,
    how
        text
        not null
        check (how IN ('Level Up', 'Item')),
    description
        text
        not null
);

